    idata = $('#data').DataTable({
        "processing": true,
        dom: 'Bfrtip',
        "ajax": {
            "headers": {
                token: getCookie("token")
            },
            "data": function (data) {
                delete data.columns;
                // Append to data
                data.active = 0;
                data.tgl_mulai = $('#period_awal').val();
                data.tgl_akhir = $('#period_akhir').val();
            },
            "url": global_var("dpath") + 'showsamplingplanrequest',
            "type": "GET"
        },
        // "deferRender": true,
        "aLengthMenu": [
            [50, 100, 200],
            [50, 100, 200]
        ],
        "autoWidth": false,
        // "responsive": true,
        "serverside": true,
        "scrollX": true,
        columnDefs: [{
            "defaultContent": "-",
            "targets": "_all"
        }],
        "columns": [{
                "render": function (data, type, row) {
                    var html = '';
                    if (permiss != null) {
                        if (permiss.data.includes('Request_Sampling_Plan_view') == true || permiss
                            .role === 1) {
                            html +=
                                '<a class="fa fa-eye pagging" href="javascript:;" mode="viewdetail" title="View Quotation"></a>'
                        }
                        if (permiss.data.includes('Request_Sampling_Plan_add') == true || permiss
                            .role === 1) {
                            html +=
                                '<a class="fa fa-plus pagging" style="padding-left:5px;" href="javascript:;" mode="aturjadwal" title="Atur Jadwal"></a>'
                        }
                    }
                    return html;
                },
                "title": "Action",
            },
            {
                "render": function (data, type, row, meta) {
                    return meta.row + meta.settings._iDisplayStart + 1;
                },
                "title": "No",
            },
            {
                "data": "no_document",
                "title": "No Document",
            },
            {
                "data": "no_quotation",
                "title": "No Quotation",
            },
            {
                "render": function (data, type, row) {
                    var html = '';
                    var datetimeString = row.opsi_1;

                    if (datetimeString === null) {
                        html = '-';
                    } else {
                        var parts = datetimeString.split(" - ");
                        var tanggal = parts[0].trim();

                        var tglDate = tanggal;

                        var startTime = '';
                        var endTime = '';

                        if (parts.length > 1) {
                            startTime = parts[1].trim();
                        }

                        if (parts.length > 2) {
                            endTime = parts[2].trim();
                        }

                        if (row.data_opsi1 === null) {
                            html = '-';
                        } else if (row.no_document.startsWith("QTC")) {
                            html = tglDate + "  " + startTime;
                        } else if (row.no_document.startsWith("QT")) {
                            if (startTime && endTime) {
                                html = startTime + " to " + tglDate + " - " + endTime;
                            } else if (startTime) {
                                html = tglDate + " - " + startTime;
                            } else if (endTime) {
                                html = tglDate + " - " + endTime;
                            }
                        } else if (row.no_document.startsWith("QTD")) {
                            // html = "QTD Case: " + tglDate;
                            html = tglDate;
                        } else {
                            // html = "Generic Case: " + tglDate;
                            html = tglDate;
                        }
                    }

                    return html;

                },
                "title": "Opsi 1",
            },
            {
                "render": function (data, type, row) {
                    var html = '';
                    var datetimeString = row.opsi_2;

                    if (datetimeString === null) {
                        html = '-';
                    } else {
                        var parts = datetimeString.split(" - ");
                        var tanggal = parts[0].trim();

                        var tglDate = tanggal;

                        var startTime = '';
                        var endTime = '';

                        if (parts.length > 1) {
                            startTime = parts[1].trim();
                        }

                        if (parts.length > 2) {
                            endTime = parts[2].trim();
                        }

                        if (row.data_opsi2 === null) {
                            html = '-';
                        } else if (row.no_document.startsWith("QTC")) {
                            html = tglDate + "  " + startTime;
                        } else if (row.no_document.startsWith("QT")) {
                            if (startTime && endTime) {
                                html = startTime + " to " + tglDate + " - " + endTime;
                            } else if (startTime) {
                                html = tglDate + " - " + startTime;
                            } else if (endTime) {
                                html = tglDate + " - " + endTime;
                            }
                        } else if (row.no_document.startsWith("QTD")) {
                            // html = "QTD Case: " + tglDate;
                            html = tglDate;
                        } else {
                            // html = "Generic Case: " + tglDate;
                            html = tglDate;
                        }
                    }

                    return html;
                },
                "title": "Opsi 2",
            },
            {
                "render": function (data, type, row) {
                    var html = '';
                    var datetimeString = row.opsi_3;

                    if (datetimeString === null) {
                        html = '-';
                    } else {
                        var parts = datetimeString.split(" - ");
                        var tanggal = parts[0].trim();

                        var tglDate = tanggal;

                        var startTime = '';
                        var endTime = '';

                        if (parts.length > 1) {
                            startTime = parts[1].trim();
                        }

                        if (parts.length > 2) {
                            endTime = parts[2].trim();
                        }

                        if (row.data_opsi3 === null) {
                            html = '-';
                        } else if (row.no_document.startsWith("QTC")) {
                            html = tglDate + "  " + startTime;
                        } else if (row.no_document.startsWith("QT")) {
                            if (startTime && endTime) {
                                html = startTime + " to " + tglDate + " - " + endTime;
                            } else if (startTime) {
                                html = tglDate + " - " + startTime;
                            } else if (endTime) {
                                html = tglDate + " - " + endTime;
                            }
                        } else if (row.no_document.startsWith("QTD")) {
                            // html = "QTD Case: " + tglDate;
                            html = tglDate;
                        } else {
                            // html = "Generic Case: " + tglDate;
                            html = tglDate;
                        }
                    }

                    return html;
                },
                "title": "Opsi 3",
            },
            {
                "render": function (data, type, row) {
                    var bulan = ['Januari', 'Februari', 'Maret', 'April', 'Mei', 'Juni', 'Juli',
                        'Agustus', 'September', 'Oktober', 'November', 'Desember'
                    ];
                    var html = '-'
                    if (row.periode_kontrak != null) {
                        html = bulan[Math.abs(row.periode_kontrak.split('-')[1]) - 1] + ' ' + row
                            .periode_kontrak.split('-')[0]
                    }
                    return html
                },
                "title": "Periode Kontrak"
            },
            {
                "data": "is_sabtu",
                "title": "Sabtu",
            },
            {
                "data": "is_minggu",
                "title": "Minggu",
            },
            {
                "data": "is_malam",
                "title": "Malam",
            }
        ],
        buttons: [{
            text: '<i class="fa fa-refresh"></i>',
            action: () => {
                idata.ajax.reload();
            },
            titleAttr: 'Refresh',
        }, ],
    })
